import { useState } from "react";
import './App.css';
import { type } from "os";

type Note = {
  id: number;
  title: string;
  content: string;
  date: string;
}

const App = () => {
  const [notes, setNotes] = useState([
    {
      id: 1,
      date: "15/07/24",
      title: "note title",
      content: "content 1",
    },
    {
      id: 2,
      date: "15/07/24",
      title: "note title 2",
      content: "content 2",
    },
    {
      id: 3,
      date: "15/07/24",
      title: "note title 3",
      content: "content 3",
    },
    {
      id: 4,
      date: "15/07/24",
      title: "note title 4",
      content: "content 4",
    },
  ]);

  const [noteTitle, setTitle]= useState("");
  const [noteContent, setContent] = useState("");

  return (
    <div className="full-app-display">
      <div className="note-display">
        <form>
          <input value={noteTitle} onChange={(event) => setTitle} placeholder="Enter note title here..." required />
          <textarea
            placeholder="Enter note content here..."
            rows={10}
            required
          ></textarea>
          <button type="submit">Save note</button>
        </form>
      </div>
      <div className="prev-notes-display">
        {notes.map((note) => (
          <div className="prev-note">
              <h2 className="title">{note.title}</h2>
              <button className="edit-button">Edit</button>
              <button className="delete-button">Delete</button>
              <p className ="pr-nt-date"> {note.date} </p>
              <p className="pr-nt-content">{note.content}</p>
        </div>
        ))}

        {/* Add more previous notes here */}

      </div>
    </div>
  );
};

export default App;
